"use strict";
// A simple type declaration
let name = "John Doe"; // string type
let age = 30; // number type
let isStudent = false; // boolean type
// Function to display user information
function displayUserInfo(userName, userAge, studentStatus) {
    console.log(`Name: ${userName}, Age: ${userAge}, Is Student: ${studentStatus}`);
}
// Calling the function
displayUserInfo(name, age, isStudent);
